mapper.select:
SELECT 

FROM doctor_flow a

WHERE a.id = #{id}

SELECT 

FROM doctor_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_date = #{createDate}


		AND a.d_name = #{dName}


		AND a.d_code = #{dCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM doctor_flow a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE doctor_flow SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
